import{a as c}from"./chunk-TS4P6BQH.js";import"./chunk-3ZVB3ZUX.js";import{a as p}from"./chunk-RQ7KEU27.js";import{V as y,a as n}from"./chunk-JWR2H664.js";import{Ha as r,Lb as g,Ma as i,fb as d}from"./chunk-4VR2QSN3.js";import{a,b as l,h as x}from"./chunk-DV6QXP24.js";var f=x(y());var D={title:"LLMs.txt",mdFile:"./index.md",order:2,category:p},e=D;var m=[];var P={},h=P;var b='<p class="ngde">LLM-optimized documentation endpoints for ngx-oneforall utilities.</p><p class="ngde">The <a href="https://llmstxt.org/" class="ngde">llms.txt</a> standard helps AI models better understand and navigate documentation. ngx-oneforall provides two files that follow this standard.</p><hr class="ngde"><h2 id="llmstxt" href="getting-started/llms" headinglink="true" class="ngde">/llms.txt<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="llmstxt"></ng-doc-heading-anchor></h2><p class="ngde">The <code class="ngde">llms.txt</code> file is a structured index of all ngx-oneforall utilities. It lists every page with a title, link, and short description \u2014 making it easy for LLMs to find relevant documentation.</p><p class="ngde"><a href="https://love1024.github.io/ngx-oneforall/llms.txt" class="ngde">Open llms.txt</a></p><hr class="ngde"><h2 id="llms-fulltxt" href="getting-started/llms" headinglink="true" class="ngde">/llms-full.txt<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="llms-fulltxt"></ng-doc-heading-anchor></h2><p class="ngde">The <code class="ngde">llms-full.txt</code> file contains the <strong class="ngde">complete documentation</strong> for all utilities in a single file. Use this when you want an LLM to have full context about the entire library.</p><p class="ngde"><a href="https://love1024.github.io/ngx-oneforall/llms-full.txt" class="ngde">Open llms-full.txt</a></p><hr class="ngde"><h2 id="regenerating" href="getting-started/llms" headinglink="true" class="ngde">Regenerating<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="regenerating"></ng-doc-heading-anchor></h2><p class="ngde">If you update documentation pages and need to regenerate the LLMs files, run:</p><pre class="shiki shiki-themes github-light ayu-dark" style="background-color:#fff;--shiki-dark-bg:#0b0e14;color:#24292e;--shiki-dark:#bfbdb6" tabindex="0"><code class="language-bash"><span class="line"><span style="color:#6F42C1;--shiki-dark:#59C2FF" class="ngde">npm</span><span style="color:#032F62;--shiki-dark:#AAD94C" class="ngde"> run</span><span style="color:#032F62;--shiki-dark:#AAD94C" class="ngde"> generate:llms</span></span></code></pre><p class="ngde">This reads all <code class="ngde">ng-doc.page.ts</code> and <code class="ngde">index.md</code> files and produces both <code class="ngde">llms.txt</code> and <code class="ngde">llms-full.txt</code> in the <code class="ngde">public/</code> directory.</p>',s=class t extends n{pageType="guide";pageContent=b;editSourceFileUrl="https://github.com/love1024/ngx-oneforall/edit/main/projects/ngx-oneforall-docs/src/app/categories/getting-started/pages/llms/index.md?message=docs(): describe your changes here...";viewSourceFileUrl="https://github.com/love1024/ngx-oneforall/blob/main/projects/ngx-oneforall-docs/src/app/categories/getting-started/pages/llms/index.md";page=e;demoAssets=h;constructor(){super()}static \u0275fac=function(o){return new(o||t)};static \u0275cmp=r({type:t,selectors:[["ng-doc-page-j3mr803t"]],features:[g([{provide:n,useExisting:t},m,e.providers??[]]),i],decls:1,vars:0,template:function(o,C){o&1&&d(0,"ng-doc-page")},dependencies:[c],encapsulation:2,changeDetection:0})},N=[l(a({},(0,f.isRoute)(e.route)?e.route:{}),{path:"",component:s,title:"LLMs.txt"})],G=N;export{s as PageComponent,G as default};
