import{a as l}from"./chunk-YGP6TIJZ.js";import"./chunk-3ZVB3ZUX.js";import{a as h}from"./chunk-RQ7KEU27.js";import{L as b,a as o}from"./chunk-JEISVZZF.js";import{Ha as i,Lb as c,Ma as d,fb as g}from"./chunk-4VR2QSN3.js";import{a as r,b as a,h as y}from"./chunk-DV6QXP24.js";var m=y(b());var v={title:"Introduction",mdFile:"./index.md",order:0,category:h},e=v;var u=[];var D={},p=D;var x=`<p class="ngde"><strong class="ngde">ngx-oneforall</strong> is a collection of 80+ high-quality Angular utilities designed to solve common development challenges. Instead of reinventing the wheel or managing dozens of small dependencies, you get a unified toolkit that just works.</p><p class="ngde"><img src="/assets/brand.png" alt="Project Image" class="ngde"></p><ng-doc-blockquote class="ngde"><p class="ngde"><strong class="ngde">One For All</strong> - A single library providing the essential blocks for modern Angular development.</p></ng-doc-blockquote><h1 id="key-features" href="getting-started/introduction" headinglink="true" class="ngde">Key Features<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="key-features"></ng-doc-heading-anchor></h1><h3 id="performance--efficiency" href="getting-started/introduction" headinglink="true" class="ngde">Performance &#x26; Efficiency<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="performance--efficiency"></ng-doc-heading-anchor></h3><p class="ngde">Every utility is written from scratch and optimized specifically for Angular. We prioritize memory efficiency and runtime performance rather than just wrapping existing heavy libraries.</p><h3 id="modular--tree-shakable" href="getting-started/introduction" headinglink="true" class="ngde">Modular &#x26; Tree-shakable<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="modular--tree-shakable"></ng-doc-heading-anchor></h3><p class="ngde">Import only what you use. The library is fully tree-shakable, so adding one utility doesn't bloat your bundle with the rest of the library.</p><h3 id="zero-dependencies" href="getting-started/introduction" headinglink="true" class="ngde">Zero Dependencies<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="zero-dependencies"></ng-doc-heading-anchor></h3><p class="ngde">We keep the dependency tree clean. <strong class="ngde">ngx-oneforall</strong> has <strong class="ngde">NO third-party dependencies</strong> (except <code class="ngde">libphonenumber-js</code> for the phone validator). This reduces security risks, simplifies updates, and prevents version conflicts.</p><h3 id="first-class-ssr-support" href="getting-started/introduction" headinglink="true" class="ngde">First-Class SSR Support<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="first-class-ssr-support"></ng-doc-heading-anchor></h3><p class="ngde">Built for modern hydration. Every component and utility is designed to work seamlessly in Server-Side Rendering (SSR) environments out of the box.</p><h3 id="100-test-coverage" href="getting-started/introduction" headinglink="true" class="ngde">100% Test Coverage<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="100-test-coverage"></ng-doc-heading-anchor></h3><p class="ngde">We take reliability seriously. The entire library is fully covered by tests to ensure edge cases are handled and behavior remains consistent across updates.</p><h3 id="built-for-modern-angular" href="getting-started/introduction" headinglink="true" class="ngde">Built for Modern Angular<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="built-for-modern-angular"></ng-doc-heading-anchor></h3><p class="ngde">Designed with <strong class="ngde">Signals</strong> and <strong class="ngde">Standalone Components</strong> in mind. This isn't a legacy port\u2014it's built for the current and future state of Angular.</p><hr class="ngde"><h1 id="why-use-it" href="getting-started/introduction" headinglink="true" class="ngde">Why Use It?<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="why-use-it"></ng-doc-heading-anchor></h1><h3 id="stop-re-writing-utilities" href="getting-started/introduction" headinglink="true" class="ngde">Stop Re-writing Utilities<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="stop-re-writing-utilities"></ng-doc-heading-anchor></h3><p class="ngde">Avoid copy-pasting the same regex patterns, validators, and helper functions between projects. Use a standardized, tested set of utilities instead.</p><h3 id="focus-on-business-logic" href="getting-started/introduction" headinglink="true" class="ngde">Focus on Business Logic<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="focus-on-business-logic"></ng-doc-heading-anchor></h3><p class="ngde">Don't waste time maintaining generic boilerplate code. Use these proven primitives to build your actual application features faster.</p><h3 id="better-developer-experience" href="getting-started/introduction" headinglink="true" class="ngde">Better Developer Experience<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="better-developer-experience"></ng-doc-heading-anchor></h3><p class="ngde">Fully typed APIs and consistent behavior make these utilities predictable and easy to work with. You get strict typing and comprehensive documentation right in your IDE.</p><hr class="ngde"><h1 id="issues--feature-requests" href="getting-started/introduction" headinglink="true" class="ngde">Issues &#x26; Feature Requests<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="issues--feature-requests"></ng-doc-heading-anchor></h1><p class="ngde">Found a bug or edge case? Have an idea for a new utility? We'd love to hear from you!</p><ul class="ngde"><li class="ngde"><strong class="ngde">Report Issues</strong>: If you encounter any bugs or unexpected behavior, please <a href="https://github.com/love1024/ngx-oneforall/issues" class="ngde">open an issue</a>.</li><li class="ngde"><strong class="ngde">Feature Requests</strong>: Have a utility in mind that would benefit the community? Submit a feature request via <a href="https://github.com/love1024/ngx-oneforall/issues" class="ngde">GitHub Issues</a>.</li></ul>`,s=class n extends o{pageType="guide";pageContent=x;editSourceFileUrl="https://github.com/love1024/ngx-oneforall/edit/main/projects/ngx-oneforall-docs/src/app/categories/getting-started/pages/introduction/index.md?message=docs(): describe your changes here...";viewSourceFileUrl="https://github.com/love1024/ngx-oneforall/blob/main/projects/ngx-oneforall-docs/src/app/categories/getting-started/pages/introduction/index.md";page=e;demoAssets=p;constructor(){super()}static \u0275fac=function(t){return new(t||n)};static \u0275cmp=i({type:n,selectors:[["ng-doc-page-ojd8g5br"]],features:[c([{provide:o,useExisting:n},u,e.providers??[]]),d],decls:1,vars:0,template:function(t,P){t&1&&g(0,"ng-doc-page")},dependencies:[l],encapsulation:2,changeDetection:0})},k=[a(r({},(0,m.isRoute)(e.route)?e.route:{}),{path:"",component:s,title:"Introduction"})],M=k;export{s as PageComponent,M as default};
